(function ($, Drupal, window, document, undefined) {

Drupal.behaviors.alwBonusVideo = {
  attach: function(context, settings) {
    // Open a bonus video player by clicking on the video thumbnail
    $('.alw-bonus-video article').click(function() {
      $video = $(this).parent();

      // Close previously opened player first
      $(this).parent().siblings().removeClass('active');
      //Show player
      Drupal.behaviors.alwBonusVideo.showPlayer($video);

    });

    // Close a bonus video player by clicking on the close button or by hitting the ESC key
    $('.alw-bonus-video .alw-bonus-player-wrapper a.bt-close').click(function(){
      $(this).parents('.alw-bonus-video').removeClass('active');
      event.preventDefault();
      return false;
    });

    $(document).bind('keydown', function(event) {
      if (event.keyCode == 27) {
        $('.alw-bonus-video .alw-bonus-player-wrapper:visible a.bt-close').click();
        return false;
      }
    });

  },

  showPlayer: function($video) {
    // Scroll to selected video
    $header = $('#arte-live-header').height();
    $clearHeader = -($header + 20);

    $.scrollTo($video, function(){
      // Open player
      $video.addClass('active');
    },{offset: $clearHeader});
  }
};


})(jQuery, Drupal, this, this.document);
