(function ($) {
  Drupal.behaviors.alwPersonal = {
    attach: function (context) {
        /**
         * Function that checks if we have 2 or more options selected and if so, disable others
         */
        function alw_personal_disable_options() {
          var categories_checked_nof = $("#edit-categories input:checked").length;

          // enable when less than 2 options selected
          if (categories_checked_nof < 2) {
            $("#categories .category").removeClass('disabled');
          }

          // disable when 2 or more options selected
          if (categories_checked_nof >= 2) {
            $("#edit-categories input").not(':checked').each(function() {
              $("#categories .category[category='" + $(this).val() + "']").addClass('disabled');
            });
          }
        }

        // mark divs as selected when corresponding checkboxes are checked
        $("#edit-categories input").each(function() {
          if ($(this).is(':checked')) {
            $("#categories .category[category='" + $(this).val() + "']").addClass('active');
          } else {
            $("#categories .category[category='" + $(this).val() + "']").removeClass('active');
          }
        });
        // check and disable options
        alw_personal_disable_options();

      // behaviour on click
      $('#categories .category').bind('click', function (event) {
        var category_name = $(this).attr('category');

        if ($(this).hasClass('active')) {
          $(this).removeClass('active');
          $("#edit-categories input[name='categories[" + category_name + "]']").removeAttr('checked');
        } else {
          // How many checkboxes have been set already ?
          var checked_categories_nof = $("#edit-categories input:checked").length;

          // Less than 2
          if (checked_categories_nof < 2) {
            $(this).addClass('active');
            $("#edit-categories input[name='categories[" + category_name + "]']").attr('checked', 'checked');
          }
        } // else

        // check and disable options
        alw_personal_disable_options();
      });

      $('.page-user-videos article.view-mode-teaser_personal').on('flagGlobalAfterLinkUpdate', function(event, data) {
        var class_name = 'nid-' + data.contentId;
        if ((data.flagStatus == 'unflagged') && (data.flagSuccess == true) && $(this).hasClass(class_name)) {
          // Remove favourite video
          $(this).hide();

          // Update counter
          var count_text_new = '(' + data.flag_count + ')';
          $('.view-id-favourite_videos .section-title span').html(count_text_new);
        }
      });


    }
  };
})(jQuery);

